import React, { useState, useEffect } from 'react';
import socket from '../services/socket';

import StockPanel from '../components/StockPanel';
import CareerPanel from '../components/CareerPanel';
import BankPanel from '../components/BankPanel';
import PlayerStats from '../components/PlayerStats';
import ActionPanel from '../components/ActionPanel';
import TurnTimer from '../components/TurnTimer';
import EventLog from '../components/EventLog';
import Leaderboard from '../components/Leaderboard';
import GameOver from '../components/GameOver';

function GamePage({ playerId, username, gameData, onBackToLobby }) {

  // ===== GAME STATE =====
  const [currentDay, setCurrentDay] = useState(1);
  const [maxDays] = useState(30);
  const [timeLeft, setTimeLeft] = useState(30);
  const [myStats, setMyStats] = useState(null);
  const [allPlayers, setAllPlayers] = useState([]);
  const [selectedAction, setSelectedAction] = useState(null);
  const [events, setEvents] = useState([]);
  const [gameOver, setGameOver] = useState(false);
  const [rankings, setRankings] = useState([]);

  // ===== EXTRA SYSTEM =====
  const [stocks, setStocks] = useState({});
  const [careers, setCareers] = useState({});
  const [activePanel, setActivePanel] = useState('game');

  // ===============================
  // INITIAL LOAD + SOCKET EVENTS
  // ===============================
  useEffect(() => {
    if (!gameData) return;

    const myPlayer = gameData.players.find(p => p.id === playerId);
    setMyStats(myPlayer?.stats || null);
    setAllPlayers(gameData.players);

    // ===== TURN START =====
    socket.on('turnStart', (data) => {
      setCurrentDay(data.day);
      setTimeLeft(Math.floor(data.timeLimit / 1000));
      setSelectedAction(null);
    });

    // ===== TURN RESULT =====
    socket.on('turnResult', (data) => {

      const myResult = data.playerResults.find(r => r.playerId === playerId);
      if (myResult) {
        setMyStats(myResult.newStats);
      }

      setAllPlayers(prev =>
        prev.map(player => {
          const result = data.playerResults.find(r => r.playerId === player.id);
          if (!result) return player;

          return {
            ...player,
            stats: result.newStats,
            alive: result.alive,
            score: result.score
          };
        })
      );

      // ===== EVENT LOG =====
      const newEvents = [];

      data.playerResults.forEach(result => {
        if (result.action) {
          newEvents.push({
            type: 'action',
            player: result.username,
            message: result.action.name,
            isMe: result.playerId === playerId
          });
        }

        if (result.event) {
          newEvents.push({
            type: 'event',
            player: result.username,
            message: result.event.message,
            isMe: result.playerId === playerId
          });
        }

        if (!result.alive) {
          newEvents.push({
            type: 'death',
            player: result.username,
            message: 'üíÄ ‡∏•‡πâ‡∏°‡∏•‡∏∞‡∏•‡∏≤‡∏¢!',
            isMe: result.playerId === playerId
          });
        }
      });

      if (data.globalEvents?.length > 0) {
        data.globalEvents.forEach(event => {
          newEvents.push({
            type: 'global',
            message: event.message
          });
        });
      }

      setEvents(prev => [...newEvents, ...prev].slice(0, 20));
    });

    socket.on('gameOver', (data) => {
      setGameOver(true);
      setRankings(data.rankings);
    });

    socket.on('actionSelected', (data) => {
      setSelectedAction(data.action);
    });

    // ===== STOCK + CAREER =====
    socket.on('stockPricesUpdate', setStocks);
    socket.on('careersData', setCareers);

    socket.emit('getStockPrices');
    socket.emit('getCareers');

    return () => {
      socket.removeAllListeners();
    };

  }, [gameData, playerId]);

  // ===============================
  // TIMER
  // ===============================
  useEffect(() => {
    if (timeLeft > 0 && !gameOver && !selectedAction) {
      const timer = setTimeout(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [timeLeft, gameOver, selectedAction]);

  // ===============================
  // HANDLERS
  // ===============================
  const handleSelectAction = (action) => {
    socket.emit('selectAction', action);
  };

  const handleBuyStock = (symbol, shares) => {
    socket.emit('buyStock', { symbol, shares });
  };

  const handleSellStock = (symbol, shares) => {
    socket.emit('sellStock', { symbol, shares });
  };

  const handleDeposit = (accountType, amount) => {
    socket.emit('depositBank', { accountType, amount });
  };

  const handleWithdraw = (accountType, amount) => {
    socket.emit('withdrawBank', { accountType, amount });
  };

  // ===============================
  // GAME OVER SCREEN
  // ===============================
  if (gameOver) {
    return (
      <GameOver
        rankings={rankings}
        playerId={playerId}
        onBackToLobby={onBackToLobby}
      />
    );
  }

  // ===============================
  // MAIN UI
  // ===============================
  return (
    <div className="game-page">

      {/* TABS */}
      <div className="game-tabs">
        <button onClick={() => setActivePanel('game')}>üéÆ ‡πÄ‡∏Å‡∏°</button>
        <button onClick={() => setActivePanel('stocks')}>üìà ‡∏´‡∏∏‡πâ‡∏ô</button>
        <button onClick={() => setActivePanel('career')}>üíº ‡∏≠‡∏≤‡∏ä‡∏µ‡∏û</button>
        <button onClick={() => setActivePanel('bank')}>üè¶ ‡∏ò‡∏ô‡∏≤‡∏Ñ‡∏≤‡∏£</button>
      </div>

      {/* GAME PANEL */}
      {activePanel === 'game' && (
        <>
          <div className="game-header">
            <h2>üéÆ Life Simulation</h2>
            <div>‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà {currentDay} / {maxDays}</div>
          </div>

          <div className="game-content">
            <div className="left-panel">
              <PlayerStats username={username} stats={myStats} isMe />
              <TurnTimer timeLeft={timeLeft} selectedAction={selectedAction} />
              <ActionPanel
                onSelectAction={handleSelectAction}
                selectedAction={selectedAction}
                disabled={!!selectedAction}
                stats={myStats}
              />
            </div>

            <div className="center-panel">
              <Leaderboard
                players={allPlayers}
                currentPlayerId={playerId}
              />
            </div>

            <div className="right-panel">
              <EventLog events={events} />
            </div>
          </div>
        </>
      )}

      {/* STOCK PANEL */}
      {activePanel === 'stocks' && (
        <StockPanel
          stocks={stocks}
          portfolio={myStats?.portfolio}
          money={myStats?.money || 0}
          onBuyStock={handleBuyStock}
          onSellStock={handleSellStock}
        />
      )}

      {/* CAREER PANEL */}
      {activePanel === 'career' && (
        <CareerPanel
          careers={careers}
          currentCareer={myStats?.career}
          currentLevel={myStats?.careerLevel}
          playerStats={myStats}
          onChooseCareer={(career) =>
            socket.emit('chooseCareer', { career })
          }
          onPromote={() => socket.emit('promoteCareer')}
        />
      )}

      {/* BANK PANEL */}
      {activePanel === 'bank' && (
        <BankPanel
          bankAccounts={myStats?.bankAccounts}
          money={myStats?.money || 0}
          onDeposit={handleDeposit}
          onWithdraw={handleWithdraw}
        />
      )}

    </div>
  );
}

export default GamePage;
