// src/pages/GamePage.js
import React, { useState, useEffect } from 'react';
import socket from '../services/socket';

import StockPanel from '../components/StockPanel';
import CareerPanel from '../components/CareerPanel';
import BankPanel from '../components/BankPanel';
import PlayerStats from '../components/PlayerStats';
import ActionPanel from '../components/ActionPanel';
import TurnTimer from '../components/TurnTimer';
import EventLog from '../components/EventLog';
import Leaderboard from '../components/Leaderboard';
import GameOver from '../components/GameOver';

function GamePage({ playerId, username, gameData, onBackToLobby }) {

  const [currentDay, setCurrentDay] = useState(1);
  const [maxDays] = useState(100);
  const [timeLeft, setTimeLeft] = useState(30);
  const [myStats, setMyStats] = useState(null);
  const [allPlayers, setAllPlayers] = useState([]);
  const [selectedAction, setSelectedAction] = useState(null);
  const [events, setEvents] = useState([]);
  const [gameOver, setGameOver] = useState(false);
  const [rankings, setRankings] = useState([]);
  const [stocks, setStocks] = useState({});
  const [careers, setCareers] = useState({});
  const [activePanel, setActivePanel] = useState('game');

  useEffect(() => {
    if (!gameData) return;

    const myPlayer = gameData.players.find(p => p.id === playerId);
    setMyStats(myPlayer?.stats || null);
    setAllPlayers(gameData.players);

  }, [gameData, playerId]);

  if (gameOver) {
    return (
      <GameOver
        rankings={rankings}
        playerId={playerId}
        onBackToLobby={onBackToLobby}
      />
    );
  }

  return (
    <div className="game-page">

      {/* ===== TABS ===== */}
      <div className="game-tabs">

        <button
          className={activePanel === 'game' ? 'active' : ''}
          onClick={() => setActivePanel('game')}
        >
          <img src="https://cdn-icons-png.flaticon.com/512/686/686589.png" alt="game" className="tab-icon" />
          เกม
        </button>

        <button
          className={activePanel === 'stocks' ? 'active' : ''}
          onClick={() => setActivePanel('stocks')}
        >
          <img src="https://cdn-icons-png.flaticon.com/512/2331/2331970.png" alt="stocks" className="tab-icon" />
          หุ้น
        </button>

        <button
          className={activePanel === 'career' ? 'active' : ''}
          onClick={() => setActivePanel('career')}
        >
          <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="career" className="tab-icon" />
          อาชีพ
        </button>

        <button
          className={activePanel === 'bank' ? 'active' : ''}
          onClick={() => setActivePanel('bank')}
        >
          <img src="https://cdn-icons-png.flaticon.com/512/483/483408.png" alt="bank" className="tab-icon" />
          ธนาคาร
        </button>

      </div>

      {/* ===== GAME PANEL ===== */}
      {activePanel === 'game' && (
        <>
          <div className="game-header">
            <h2 className="game-title">
              <img src="https://cdn-icons-png.flaticon.com/512/686/686589.png" alt="logo" className="header-icon" />
              Life Simulation
            </h2>
            <div className="day-counter">
              วันที่ {currentDay} / {maxDays}
            </div>
          </div>

          <div className="game-content">
            <div className="left-panel">
              <PlayerStats username={username} stats={myStats} isMe />
              <TurnTimer timeLeft={timeLeft} selectedAction={selectedAction} />
              <ActionPanel
                selectedAction={selectedAction}
                disabled={!!selectedAction}
                stats={myStats}
              />
            </div>

            <div className="center-panel">
              <Leaderboard
                players={allPlayers}
                currentPlayerId={playerId}
              />
            </div>

            <div className="right-panel">
              <EventLog events={events} />
            </div>
          </div>
        </>
      )}

      {/* ===== STOCK PANEL ===== */}
      {activePanel === 'stocks' && (
        <StockPanel
          stocks={stocks}
          portfolio={myStats?.portfolio}
          money={myStats?.money || 0}
        />
      )}

      {/* ===== CAREER PANEL ===== */}
      {activePanel === 'career' && (
        <CareerPanel
          careers={careers}
          currentCareer={myStats?.career}
          currentLevel={myStats?.careerLevel}
          playerStats={myStats}
        />
      )}

      {/* ===== BANK PANEL ===== */}
      {activePanel === 'bank' && (
        <BankPanel
          bankAccounts={myStats?.bankAccounts}
          money={myStats?.money || 0}
        />
      )}

    </div>
  );
}

export default GamePage;
