// src/pages/LobbyPage.js
import React, { useState } from 'react';
import socket from '../services/socket';

function LobbyPage({ username, playerId, lobbyData, setLobbyData }) {
  const [searching, setSearching] = useState(false);
  const [lobbyId, setLobbyId] = useState('');

  const handleQuickMatch = () => {
    setSearching(true);
    socket.findGame();

    socket.on('searching', () => {
      console.log('‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏´‡∏≤‡πÄ‡∏Å‡∏°...');
    });
  };

  const handleCancelSearch = () => {
    setSearching(false);
    socket.cancelSearch();
  };

  const handleCreateRoom = () => {
    socket.createRoom(4);
  };

  const handleJoinRoom = () => {
    if (lobbyId.trim()) {
      socket.joinRoom(lobbyId.trim());
    }
  };

  const handleReady = () => {
    socket.ready();
  };

  const handleLeaveRoom = () => {
    socket.leaveRoom();
    setLobbyData(null);
  };

  const isPlayerReady = (player) => {
    return player.ready;
  };

  const currentPlayer = lobbyData?.players?.find(p => p.id === playerId);

  /* =========================
      ‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏´‡πâ‡∏≠‡∏á‡πÅ‡∏•‡πâ‡∏ß
  ========================== */
