    return (
      <div className="lobby-page">
        <div className="lobby-container">

          <h2>üè† ‡∏´‡πâ‡∏≠‡∏á‡∏£‡∏≠</h2>
          <p className="lobby-id">
            ‡∏£‡∏´‡∏±‡∏™‡∏´‡πâ‡∏≠‡∏á: <strong>{lobbyData.lobbyId}</strong>
          </p>

          <div className="players-list">
            <h3>‡∏ú‡∏π‡πâ‡πÄ‡∏•‡πà‡∏ô ({lobbyData.players.length}/4)</h3>

            {lobbyData.players.map(player => (
              <div key={player.id} className="player-item">
                <span className="player-name">
                  {player.username} {player.id === playerId && '(‡∏Ñ‡∏∏‡∏ì)'}
                </span>

                <span
                  className={`player-status ${
                    isPlayerReady(player) ? 'ready' : 'waiting'
                  }`}
                >
                  {isPlayerReady(player) ? '‚úÖ ‡∏û‡∏£‡πâ‡∏≠‡∏°' : '‚è≥ ‡∏£‡∏≠'}
                </span>
              </div>
            ))}
          </div>

          <div className="lobby-actions">
            {!currentPlayer?.ready ? (
              <button className="btn-ready" onClick={handleReady}>
                ‚úÖ ‡∏û‡∏£‡πâ‡∏≠‡∏°
              </button>
            ) : (
              <p className="waiting-text">‚è≥ ‡∏£‡∏≠‡∏ú‡∏π‡πâ‡πÄ‡∏•‡πà‡∏ô‡∏Ñ‡∏ô‡∏≠‡∏∑‡πà‡∏ô...</p>
            )}

            <button className="btn-leave" onClick={handleLeaveRoom}>
              ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏´‡πâ‡∏≠‡∏á
            </button>
          </div>

          <div className="lobby-tip">
            üí° ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏ó‡∏∏‡∏Å‡∏Ñ‡∏ô‡∏Å‡∏î "‡∏û‡∏£‡πâ‡∏≠‡∏°" ‡πÄ‡∏Å‡∏°‡∏à‡∏∞‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ó‡∏±‡∏ô‡∏ó‡∏µ!
          </div>
        </div>
      </div>
    );
  }

  /* =========================
      ‡∏´‡∏ô‡πâ‡∏≤ Lobby ‡∏´‡∏•‡∏±‡∏Å
  ========================== */
  return (
    <div className="lobby-page">
      <div className="lobby-container">

        <h1>üéÆ Life Simulation</h1>
        <p className="welcome">
          ‡∏¢‡∏¥‡∏ô‡∏î‡∏µ‡∏ï‡πâ‡∏≠‡∏ô‡∏£‡∏±‡∏ö, <strong>{username}</strong>!
