            <button className="btn-secondary" onClick={handleCreateRoom}>
              üè† ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏´‡πâ‡∏≠‡∏á
            </button>

            <div className="join-room">
              <input
                type="text"
                placeholder="‡πÉ‡∏™‡πà‡∏£‡∏´‡∏±‡∏™‡∏´‡πâ‡∏≠‡∏á"
                value={lobbyId}
                onChange={(e) => setLobbyId(e.target.value)}
              />
              <button
                onClick={handleJoinRoom}
                disabled={!lobbyId.trim()}
              >
                ‡πÄ‡∏Ç‡πâ‡∏≤‡∏´‡πâ‡∏≠‡∏á
              </button>
            </div>
          </div>
        ) : (
          <div className="searching-container">
            <div className="spinner"></div>
            <p>üîç ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏´‡∏≤‡∏ú‡∏π‡πâ‡πÄ‡∏•‡πà‡∏ô...</p>
            <button className="btn-cancel" onClick={handleCancelSearch}>
              ‡∏¢‡∏Å‡πÄ‡∏•‡∏¥‡∏Å
            </button>
          </div>
        )}

        {/* üî• ‡∏Å‡∏ï‡∏¥‡∏Å‡∏≤ (‡πÉ‡∏ä‡πâ‡πÑ‡∏≠‡∏Ñ‡∏≠‡∏ô‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÅ‡∏ó‡∏ô‡∏≠‡∏µ‡πÇ‡∏°‡∏à‡∏¥) */}
        <div className="lobby-info">
          <h3>üéØ ‡∏Å‡∏ï‡∏¥‡∏Å‡∏≤</h3>
          <ul>
            <li>
              <img
                src="https://cdn-icons-png.flaticon.com/512/3135/3135706.png"
                alt="players"
