// src/pages/GamePage.js - UPDATED VERSION with Advanced Systems
import React, { useState, useEffect } from 'react';
import socket from '../services/socket';
import PlayerStats from '../components/PlayerStats';
import ActionPanel from '../components/ActionPanel';
import TurnTimer from '../components/TurnTimer';
import EventLog from '../components/EventLog';
import Leaderboard from '../components/Leaderboard';
import GameOver from '../components/GameOver';
import StockPanel from '../components/StockPanel';
import CareerPanel from '../components/CareerPanel';
import BankPanel from '../components/BankPanel';

function GamePage({ playerId, username, gameData, onBackToLobby }) {
  const [currentDay, setCurrentDay] = useState(1);
  const [maxDays] = useState(30);
  const [timeLeft, setTimeLeft] = useState(30);
  const [myStats, setMyStats] = useState(null);
  const [allPlayers, setAllPlayers] = useState([]);
  const [selectedAction, setSelectedAction] = useState(null);
  const [events, setEvents] = useState([]);
  const [gameOver, setGameOver] = useState(false);
  const [rankings, setRankings] = useState([]);

  // Advanced Systems State
  const [activePanel, setActivePanel] = useState('game'); // game, stocks, career, bank
  const [stocks, setStocks] = useState({});
  const [careers, setCareers] = useState({});

  useEffect(() => {
    if (gameData) {
      // ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô
      const myPlayer = gameData.players.find(p => p.id === playerId);
      setMyStats(myPlayer?.stats);
      setAllPlayers(gameData.players);

      // ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏£‡∏∞‡∏ö‡∏ö‡∏Ç‡∏±‡πâ‡∏ô‡∏™‡∏π‡∏á
      if (gameData.stocks) {
        setStocks(gameData.stocks);
      }
      if (gameData.careers) {
        setCareers(gameData.careers);
      }
    }

    // ‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏ó‡∏¥‡∏£‡πå‡∏ô
    socket.on('turnStart', (data) => {
      setCurrentDay(data.day);
      setTimeLeft(Math.floor(data.timeLimit / 1000));
      setSelectedAction(null);
    });

    // ‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡πÄ‡∏ó‡∏¥‡∏£‡πå‡∏ô
    socket.on('turnResult', (data) => {
      console.log('Turn Result:', data);

      // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏™‡πÄ‡∏ï‡∏ï‡∏±‡∏™
      const myResult = data.playerResults.find(r => r.playerId === playerId);
      if (myResult) {
        setMyStats(myResult.newStats);
      }

      // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏ú‡∏π‡πâ‡πÄ‡∏•‡πà‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
      setAllPlayers(prev => {
        return prev.map(player => {
          const result = data.playerResults.find(r => r.playerId === player.id);
          if (result) {
            return {
              ...player,
              stats: result.newStats,
              alive: result.alive,
              score: result.score
            };
          }
          return player;
        });
      });

      // ‡πÄ‡∏û‡∏¥‡πà‡∏° event log
      const newEvents = [];

      data.playerResults.forEach(result => {
        if (result.action) {
          newEvents.push({
            type: 'action',
            player: result.username,
            message: `${result.action.name}`,
            isMe: result.playerId === playerId
          });
        }

        if (result.event) {
          newEvents.push({
            type: 'event',
            player: result.username,
            message: result.event.message,
            isMe: result.playerId === playerId
          });
        }

        if (!result.alive) {
          newEvents.push({
            type: 'death',
            player: result.username,
            message: 'üíÄ ‡∏•‡πâ‡∏°‡∏•‡∏∞‡∏•‡∏≤‡∏¢!',
            isMe: result.playerId === playerId
          });
        }
      });

      if (data.globalEvents?.length > 0) {
        data.globalEvents.forEach(event => {
          newEvents.push({
            type: 'global',
            message: event.message
          });
        });
      }

      setEvents(prev => [...newEvents, ...prev].slice(0, 20));
    });

    // ‡∏à‡∏ö‡πÄ‡∏Å‡∏°
    socket.on('gameOver', (data) => {
      setGameOver(true);
      setRankings(data.rankings);
    });

    // Action ‡∏ñ‡∏π‡∏Å‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
    socket.on('actionSelected', (data) => {
      setSelectedAction(data.action);
    });

    // === Advanced Systems Events ===

    // Stock Market
    socket.on('stockPricesUpdate', (data) => {
      setStocks(data);
    });

    socket.on('stockTradeResult', (data) => {
      if (data.success) {
        // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡πÄ‡∏á‡∏¥‡∏ô‡πÅ‡∏•‡∏∞‡∏û‡∏≠‡∏£‡πå‡∏ï‡πÇ‡∏ü‡∏•‡∏¥‡πÇ‡∏≠
        socket.emit('getStockPrices');
      } else {
        alert(data.reason || '‡∏ó‡∏≥‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
      }
    });

