// src/App.js - UPDATED VERSION with Advanced Systems
import React, { useState, useEffect } from 'react';
import socket from './services/socket';
import LoginPage from './pages/LoginPage';
import LobbyPage from './pages/LobbyPage';
import GamePage from './pages/GamePage';
import './styles/App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // login, lobby, game
  const [playerId, setPlayerId] = useState(null);
  const [username, setUsername] = useState('');
  const [lobbyData, setLobbyData] = useState(null);
  const [gameData, setGameData] = useState(null);

  useEffect(() => {
    // à¹€à¸Šà¸·à¹ˆà¸­à¸¡à¸•à¹ˆà¸­ Socket
    socket.connect();

    // à¸¥à¸‡à¸—à¸°à¹€à¸šà¸µà¸¢à¸™à¸ªà¸³à¹€à¸£à¹‡à¸ˆ
    socket.on('registered', (data) => {
      console.log('âœ… Registered successfully:', data);
      setPlayerId(data.playerId);
      setUsername(data.username);
      setCurrentPage('lobby');
    });

    // à¸žà¸šà¸«à¹‰à¸­à¸‡à¹à¸¥à¹‰à¸§
    socket.on('lobbyFound', (data) => {
      setLobbyData(data);
    });

    // à¸«à¹‰à¸­à¸‡à¸–à¸¹à¸à¸ªà¸£à¹‰à¸²à¸‡
    socket.on('roomCreated', (data) => {
      setLobbyData(data);
    });

    // à¸¡à¸µà¸„à¸™à¹€à¸‚à¹‰à¸²à¸«à¹‰à¸­à¸‡
    socket.on('playerJoined', (data) => {
      setLobbyData(prev => ({ ...prev, players: data.players }));
    });

    // à¸¡à¸µà¸„à¸™à¸žà¸£à¹‰à¸­à¸¡
    socket.on('playerReady', (data) => {
      setLobbyData(prev => ({ ...prev, players: data.players }));
    });

    // à¹€à¸à¸¡à¹€à¸£à¸´à¹ˆà¸¡
    socket.on('gameStarted', (data) => {
      setGameData(data);
      setCurrentPage('game');
    });

    // à¸¡à¸µà¸„à¸™à¸­à¸­à¸
    socket.on('playerLeft', (data) => {
      setLobbyData(prev => ({ ...prev, players: data.players }));
    });

    // Error
    socket.on('error', (data) => {
      alert(data.message);
    });

    return () => {
      socket.removeAllListeners('registered');
      socket.removeAllListeners('lobbyFound');
      socket.removeAllListeners('roomCreated');
      socket.removeAllListeners('playerJoined');
      socket.removeAllListeners('playerReady');
      socket.removeAllListeners('gameStarted');
      socket.removeAllListeners('playerLeft');
      socket.removeAllListeners('error');
    };
  }, []);

  const handleLogin = (name) => {
    console.log('ðŸ”µ Attempting login with username:', name);
    socket.register(name);
  };

  const handleBackToLobby = () => {
    setCurrentPage('lobby');
    setLobbyData(null);
    setGameData(null);
    socket.leaveRoom();
  };

  return (
    <div className="App">
      {currentPage === 'login' && (
        <LoginPage onLogin={handleLogin} />
      )}

      {currentPage === 'lobby' && (
        <LobbyPage 
          username={username}
          playerId={playerId}
          lobbyData={lobbyData}
          setLobbyData={setLobbyData}
        />
      )}

      {currentPage === 'game' && (
        <GamePage 
          playerId={playerId}
          username={username}
          gameData={gameData}
          onBackToLobby={handleBackToLobby}
        />
      )}
    </div>
  );
}

export default App;
