  const getMedalEmoji = (index) => {
    if (index === 0) return 'ü•á';
    if (index === 1) return 'ü•à';
    if (index === 2) return 'ü•â';
    return `${index + 1}.`;
  };

  return (
    <div className="leaderboard">
      <h3>üèÜ ‡∏≠‡∏±‡∏ô‡∏î‡∏±‡∏ö‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô</h3>

      <div className="leaderboard-list">
        {sortedPlayers.map((player, index) => (
          <div 
            key={player.id}
            className={`leaderboard-item ${player.id === currentPlayerId ? 'current-player' : ''} ${!player.alive ? 'dead' : ''}`}
          >
            <div className="player-rank">
              {getMedalEmoji(index)}
            </div>

            <div className="player-info">
              <div className="player-name">
                {player.username}
                {player.id === currentPlayerId && ' (‡∏Ñ‡∏∏‡∏ì)'}
                {!player.alive && ' üíÄ'}
              </div>
              <div className="player-mini-stats">
                üí∞ {Math.round(player.stats?.money || 0)} |
                üòä {Math.round(player.stats?.happiness || 0)} |
                ‚ù§Ô∏è {Math.round(player.stats?.health || 0)}
              </div>
            </div>

            <div className="player-score">
              <strong>{Math.round(player.score || 0)}</strong>
              <span className="score-label">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Leaderboard;
